mapper.select:
SELECT order_date, COUNT(*) AS order_total, SUM(order_goods_price) as revenue_total
FROM tb_order where order_date >= #{startdate_daily} and order_date <= #{enddate_daily}
GROUP BY order_date
ORDER BY order_date;

SELECT to_char(order_date, 'YYYY-MM') as order_date, COUNT(*) AS order_total, SUM(order_goods_price) AS revenue_total
FROM  tb_order WHERE order_date >= #{startdate_monthly} AND order_date <= #{enddate_monthly} 
GROUP BY to_char(order_date, 'YYYY-MM')
ORDER BY to_char(order_date, 'YYYY-MM');
